def main():
    """Main setup function"""
    print("=" * 60)
    print("🎯 Very Lonely CNS - Setup Script")
    print("=" * 60)
    print("This script will help you set up the standalone CNS Image Processor.\n")
    
    # Check Python version
    python_version = sys.version_info
    print(f"🐍 Python {python_version.major}.{python_version.minor}.{python_version.micro}")
    if python_version < (3, 7):
        print("❌ Python 3.7+ required")
        return False
    
    # Install dependencies
    if not install_requirements():
        return False
    
    print("\n" + "=" * 60)
    print("🔍 VERIFICATION")
    print("=" * 60)
    
    # Check dependencies
    deps_ok = check_dependencies()
    pytorch_ok = check_pytorch()
    cns_ok = check_cns_modules()
    checkpoints_ok = check_checkpoints()
    
    if not (deps_ok and pytorch_ok and cns_ok and checkpoints_ok):
        print("\n❌ Some components are missing. Please check the errors above.")
        return False
    
    print("\n" + "=" * 60)
    print("🧪 TESTING")
    print("=" * 60)
    
    # Run test
    test_ok = run_test()
    
    if test_ok:
        print("\n" + "=" * 60)
        print("🎉 SETUP COMPLETE!")
        print("=" * 60)
        print("Very Lonely CNS is ready to use!")
        print("\nQuick start:")
        print("  python cns_image_processor.py --goal goal.jpg --current current.jpg")
        print("  python example_usage.py")
        print("\nFor more information, see README.md")
        return True
    else:
        print("\n❌ Setup completed with errors. Check the test output above.")
        return False

